          opacity: 0,
          offset : "-=1200",
          easing: 'easeOutExpo',
        })
      
        .add({
          targets :  ".intro-elem",
          offset : '-=700',
          translateY : 0,
          easing: 'easeOutExpo',
          elasticity:500,
           complete : function() {
              _this.isComplete = true;
              _this.$emit("onAnimationFinish");
              _this.$store.commit("completeIntroAnimation");
          }
        })
        .add({
          targets:".intro-elem",
          opacity: 1,
          easing: "easeInOutQuint",
        })
        .add({
          targets: '.name_hero ',
          translateY: [-100,0],
          offset : '-=1200',
          easing: "easeOutExpo",
          duration: 1400,
          delay: function(el, i) {
            return 30 * i;
          }
        })
        .add({
          targets: '.intro_container_social svg',
          translateY: [0, 100],
          opacity : [0 , 1],
          offset : '-=1000',
          easing: "easeOutExpo",
          duration: 1400,
          delay: function(el, i) {
            return 30 * i;
          }
        })
    },
    normalAnimation(){
       let _this = this;
       this.$anime
        .timeline()
        .add({
          targets : ".rounded_item",
          scale : [0, 1],
          easing: "easeOutExpo",
          elasticity:500,
          opacity : [1 , 0],
          duration : 2500,
        })
        .add({
          targets :  ".intro-elem svg",
          offset : '-=2600',
          translateY : 0,
          easing: 'easeOutExpo',
          elasticity:500,
           complete : function() {
              _this.isComplete = true;
              _this.$emit("onAnimationFinish");
          }
        })
        .add({
          targets:".intro-elem svg path",
          offset: '-=1000',
          fill : function(el,i) {
            if (i == 0 || i == 12 || i == 3) {
              return "#ff174e";
            } else {
              return "#001f4f";
            }
          },
        })
        .add({
          targets : ".intro-elem .hi_elem path",
          opacity: [
              { value: 1, easing: 'easeInOutSine' }
          ],
          strokeWidth : 0
        })
        .add(this.animate_strok(".intro-elem .name_elem path"))
        .add({
          targets : ".intro-elem .name_elem",
          translateY : -20,
          opacity: [
              { value: 1, easing: 'easeInOutSine' }
          ],
          offset: '-=4000'
        })
        .add({
          offset: '-=2500',
          targets : ".intro-elem .fullstack_elem path",
          strokeWidth: 0,
          opacity : 1,
           translateY: [-100,0],
          easing: "easeOutExpo",
          duration: 1400,
          delay: function(el, i) {
            return 30 * i;
          }
        })
        .add({
          targets:".intro-elem svg path",
          offset: '-=500',
          fill : function(el,i) {
            if (i == 0 || i == 12 || i == 3) {
              return "#ff174e";
            } else {
              return "#001f4f";
            }
          },
        })
        .add({
          targets:".intro-elem",
          opacity: 1,
          easing: "easeInOutQuint",
        })
    },
    animate_strok(target) {
       let _this = this;
      return {
          targets: target,
          strokeDashoffset: [_this.$anime.setDashoffset, 5],
          easing: 'easeInOutSine',
          opacity : [1 , 1],
          duration: 2500,
          delay: function(el, i) { return i * 250 },
          direction: 'alternate',
          strokeWidth: [4, 0],
          offset : "-=1000",
          delay: function(target, index) {
            return index * 100;
          },
          elasticity: function(target, index, totalTargets) {
            return 200 + ((totalTargets - index) * 200);
          },
           fill : function(el, i) {
              if(i == 2 || i == 10 ) {
                return "#ff174e"; 
              } else {
                  return "#fff";
              }
          },
        };
    }
  },

}
</script>

